import React, {useState, useEffect} from "react"
import Item from "./Item"
const List = () =>{
    const [heroObject, setHeroObject] = useState([])

    const handleRemove = (name) => {
        console.log(name);
        console.log(heroObject);

        heroObject.filter((hero, index)=>{
            if( hero.name !== name){
                return 
            }
        })
        setHeroObject(heroObject)



    }

    useEffect(() => {
        fetch(window.location.pathname+'list')
    .then(result => result.json())
    .then(data => setHeroObject(data));
        return () => {

        }
    }, [heroObject])

    return(
        <ul>
            {heroObject.map((hero, index) => <Item delete={(name)=> handleRemove(name)} hero = {hero} key ={index} />)}
        </ul>
    )
}

export default List
