import React from "react"


const Item = (props: { hero: { name: React.ReactNode; superpower: React.ReactNode } }) => {

const handleRemove = () => {
    console.log(props.hero.name);
    fetch(window.location.pathname+'delete/'+props.hero.name, {
        method: 'DELETE',
    }).then(()=> props.delete(props.hero.name))


}

    return(
        <>
        <h1>{props.hero.name}</h1>
        <h2>{props.hero.superpower}</h2>
        <button onClick={handleRemove}> remove</button>
        </>
    )
}

export default Item
