import React, {useState, useEffect} from "react"
import Item from "./Item"
const List = () =>{
    const [heroObject, setHeroObject] = useState([])

    const handleRemove = (name) => {
        console.log(name);

        for(let i = 0; i< heroObject; i++){
            console.log(heroObject[i]);

        }

    }

    useEffect(() => {
        fetch(window.location.pathname+'list')
    .then(result => result.json())
    .then(data => setHeroObject(data));
        return () => {

        }
    }, [])

    return(
        <ul>
            {heroObject.map((hero, index) => <Item delete={(name)=> handleRemove(name)} hero = {hero} key ={index} />)}
        </ul>
    )
}

export default List
